--

CREATE TABLE `monster` (
  `monsterId` int(3) NOT NULL COMMENT 'the unique heroId used as a parameter in the URL and fetched by PHP using the $_GET superblobal variable',
  `monsterName` varchar(50) NOT NULL COMMENT 'the name of the monster, just a string',
  `monsterDesc` text NOT NULL COMMENT 'some information of the hero, just a string',
  `monsterPower` text NOT NULL,
  `monsterImage` varchar(50) NOT NULL COMMENT 'the image of the hero is strored as a string. The actual image is strored on the server. Use the string as the source of the HTML img-tag.',
  `challengeRating` float NOT NULL COMMENT 'this is the teamId. Used as a referenc to the team table.',
  `monsterType` varchar(50) NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=latin1;

